import unittest
from src.day06_tuning_trouble.part1_2 import detect_position


class TestDay6(unittest.TestCase):

    def test_part1_with_samples(self):
        self.assertEqual(5, detect_position("bvwbjplbgvbhsrlpgdmjqwftvncz", 4))
        self.assertEqual(6, detect_position("nppdvjthqldpwncqszvftbrmjlhg", 4))
        self.assertEqual(10, detect_position("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 4))
        self.assertEqual(11, detect_position("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 4))

    def test_part1_with_input(self):
        self.assertEqual(1042, detect_position("", 4))

    def test_part2_with_samples(self):
        self.assertEqual(19, detect_position("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 14))
        self.assertEqual(23, detect_position("bvwbjplbgvbhsrlpgdmjqwftvncz", 14))
        self.assertEqual(23, detect_position("nppdvjthqldpwncqszvftbrmjlhg", 14))
        self.assertEqual(29, detect_position("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 14))
        self.assertEqual(26, detect_position("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 14))

    def test_part2_with_input(self):
        self.assertEqual(2980, detect_position("", 14))

    def test_part1_2_no_pattern(self):
        self.assertEqual(None, detect_position("aabbccddeeffgghhiijjkkllmm", 4))


if __name__ == '__main__':
    unittest.main()
